import React from 'react';

const Sidebar = ({ activeUsers = [], setSelectedUser, selectedUser }) => {
    return (
        <div>
            <h4>Active Users</h4>
            <ul>
                {activeUsers.length > 0 ? activeUsers.map((user) => (
                    <li
                        key={user.socketId}
                        onClick={() => setSelectedUser(user)}
                        style={{
                            cursor: 'pointer',
                            backgroundColor: selectedUser?.socketId === user.socketId ? '#ccc' : 'transparent',
                        }}
                    >
                        {user.username}
                    </li>
                )) : <p>No active users</p>}
            </ul>
        </div>
    );
};

export default Sidebar;
