import React, { useState, useEffect } from 'react';
import io from 'socket.io-client';
import Sidebar from '../components/Sidebar.js'; // Import the Sidebar component
import ChatInput from './ChatInput';
import Message from './Message';
import '../styles/Chat.css'; // Add custom CSS for the chat
import socketIOClient from 'socket.io-client';

const Chat = () => {
  const [users, setUsers] = useState([]);

  useEffect(() => {
    const socket = socketIOClient('http://localhost:5000'); // Adjust URL if needed

    socket.on('activeUsers', (activeUsers) => {
      setUsers(activeUsers); // Update the users state when receiving data
    });

    return () => {
      socket.disconnect();
    };
  }, []);

  return (
    <div className="chat-container">
      <Sidebar users={users} />
      {/* Other chat components */}
    </div>
  );
};

export default Chat;
