import React, { useState } from "react";
import '../styles/login.css'; // Import the CSS file for styling
import Chat from './Chat';

const Login = () => {
  const [username, setUsername] = useState("");
  const [password, setPassword] = useState(""); // Added password field
  const [isLoggedIn, setIsLoggedIn] = useState(false);

  const handleLogin = () => {
    if (username && password) {
      setIsLoggedIn(true); // Set logged in state if username and password are entered
    }
  };

  return (
    <div className="login-container">
      {!isLoggedIn ? (
        <div className="login-box">
          <h2>Login</h2>
          <input
            type="text"
            placeholder="email address"
            value={username}
            onChange={(e) => setUsername(e.target.value)}
            className="input-field"
          />
          <input
            type="password"
            placeholder="password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            className="input-field"
          />
          <button onClick={handleLogin} className="login-button">Login</button>
          <p className="signup-link">Sign Up</p>
        </div>
      ) : (
        <Chat username={username} /> // Pass the entered username to Chat component
      )}
    </div>
  );
};

export default Login;
