import React, { useState, useEffect, useCallback } from 'react';
import io from 'socket.io-client';
import ChatInput from './ChatInput';
import Message from './Message';
import "../styles/Chat.css";

const socket = io('http://localhost:3001');

function Chat({ username }) {
    const [message, setMessage] = useState('');
    const [messages, setMessages] = useState([]);
    const [activeUsers, setActiveUsers] = useState([]);
    const [selectedUser, setSelectedUser] = useState('');

    const handleSubmit = useCallback((e) => {
        e.preventDefault();

        if (message.trim() && selectedUser) {
            socket.emit('send_private_message', {
                sender: username,
                receiver: selectedUser,
                message,
            });
            setMessages((prevMessages) => [...prevMessages, { sender: username, message }]);
            setMessage(''); // Clear the input field
        }
    }, [message, selectedUser, username]);

    useEffect(() => {
        // When the user joins, emit the join event
        socket.emit('join', username);

        // Update the active users list when receiving the event from the server
        socket.on('updateUserList', (users) => {
            const uniqueUsers = users.filter((user, index, self) =>
                index === self.findIndex((u) => u.username === user.username)
            );
            setActiveUsers(uniqueUsers);
        });

        // Listen for private messages
        socket.on('receive_private_message', (data) => {
            setMessages((prevMessages) => [...prevMessages, data]);
        });

        // Clean up when the component unmounts
        return () => {
            socket.off('updateUserList');
            socket.off('receive_private_message');
        };
    }, [username]);

    return (
        <div className="chat-container">
            <div className="sidebar">
                <h2>Welcome, {username}</h2>
                <div className="user-list">
                    <h3>Active Users</h3>
                    <ul>
                        {activeUsers.map((user) => (
                            <li
                                key={user.socketId}
                                onClick={() => setSelectedUser(user.username)}
                                className={selectedUser === user.username ? 'active' : ''}
                            >
                                {user.username}
                            </li>
                        ))}
                    </ul>
                </div>
                <button className="logout-button">Logout</button>
            </div>

            <div className="chat-box">
                {selectedUser ? (
                    <>
                        <h3>Chat with {selectedUser}</h3>
                        <div className="messages">
                            {messages.map((msg, index) => (
                                <Message
                                    key={index}
                                    text={msg.message}
                                    time={new Date().toLocaleTimeString()}
                                    isSender={msg.sender === username}
                                />
                            ))}
                        </div>
                        <ChatInput
                            message={message}
                            setMessage={setMessage}
                            handleSubmit={handleSubmit} // Pass handleSubmit as is
                        />
                    </>
                ) : (
                    <div className="placeholder">
                        <p>Select a user to start chatting.</p>
                    </div>
                )}
            </div>
        </div>
    );
}

export default Chat;
