import React, { useEffect, useState } from 'react';
import io from 'socket.io-client';

const socket = io('http://localhost:5000');

const Chat = ({ username }) => {
  const [message, setMessage] = useState('');
  const [messages, setMessages] = useState([]);
  const [activeUsers, setActiveUsers] = useState([]);

  useEffect(() => {
    socket.emit('join', username); // Notify server about the new user

    socket.on('activeUsers', (users) => {
      setActiveUsers(users); // Update active users
    });

    return () => {
      socket.off('activeUsers'); // Clean up
    };
  }, [username]);

  const sendMessage = (e) => {
    e.preventDefault();
    if (message) {
      const newMessage = { sender: username, content: message };
      setMessages((prev) => [...prev, newMessage]);
      setMessage('');

      // Optionally emit the message to the server to broadcast it to others
      // socket.emit('chat message', newMessage);
    }
  };

  return (
    <div className="chat-container">
      <div className="active-users">
        <h3>Active Users</h3>
        <ul>
          {activeUsers.map((user, index) => (
            <li key={index}>{user}</li>
          ))}
        </ul>
      </div>
      <div className="messages">
        <h3>Messages</h3>
        <ul>
          {messages.map((msg, index) => (
            <li key={index}>
              <strong>{msg.sender}: </strong>{msg.content}
            </li>
          ))}
        </ul>
      </div>
      <form onSubmit={sendMessage}>
        <input
          type="text"
          value={message}
          onChange={(e) => setMessage(e.target.value)}
          placeholder="Type a message..."
          onKeyPress={(e) => e.key === 'Enter' && sendMessage(e)}
        />
        <button type="submit">Send</button>
      </form>
    </div>
  );
};

export default Chat;
