import React, { useState, useEffect } from "react";
import io from "socket.io-client";
import"../styles/chat.css"

const socket = io("http://localhost:5000");

const Chat = ({ username }) => {
  const [activeUsers, setActiveUsers] = useState([]);
  const [currentChat, setCurrentChat] = useState(null);
  const [message, setMessage] = useState("");
  const [chatMessages, setChatMessages] = useState([]);

  useEffect(() => {
    // Join user on login
    socket.emit("join", { username });

    // Listen for active users
    socket.on("activeUsers", (users) => {
      setActiveUsers(users);
    });

    // Listen for incoming messages
    socket.on("message", (messageData) => {
      if (messageData.from === currentChat || messageData.to === currentChat) {
        setChatMessages((prevMessages) => [...prevMessages, messageData]);
      }
    });

    return () => {
      socket.off();
    };
  }, [username, currentChat]);

  // Handle sending a message
  const handleSendMessage = () => {
    if (message.trim() !== "") {
      socket.emit("sendMessage", { to: currentChat, message });
      setChatMessages([...chatMessages, { from: username, message }]);
      setMessage("");
    }
  };

  // Render active users in the sidebar
  const renderActiveUsers = () => {
    return activeUsers.map((user) => (
      <div
        key={user.username}
        onClick={() => setCurrentChat(user.username)}
        className={currentChat === user.username ? "active-user selected" : "active-user"}
      >
        {user.username}
      </div>
    ));
  };

  return (
    <div className="chat-container">
      <div className="sidebar">
        <h3>Active Users</h3>
        {renderActiveUsers()}
      </div>
      <div className="chat-box">
        <div className="messages">
          {currentChat ? (
            chatMessages.map((msg, index) => (
              <div
                key={index}
                className={msg.from === username ? "message me" : "message them"}
              >
                <span>{msg.from}: {msg.message}</span>
              </div>
            ))
          ) : (
            <p>Select a user to start chatting</p>
          )}
        </div>
        {currentChat && (
          <div className="input-container">
            <input
              type="text"
              value={message}
              onChange={(e) => setMessage(e.target.value)}
              placeholder="Type a message..."
            />
            <button onClick={handleSendMessage}>Send</button>
          </div>
        )}
      </div>
    </div>
  );
};

export default Chat;
