import React, { useState, useEffect } from "react";
import io from "socket.io-client";
import "../styles/Chat.css";

const socket = io.connect("http://localhost:5000"); // Backend connection

const Chat = ({ username }) => {
  const [message, setMessage] = useState("");
  const [messages, setMessages] = useState([]);

  useEffect(() => {
    // Listen for incoming messages from the server
    socket.on("receive_message", (data) => {
      setMessages((prevMessages) => [...prevMessages, data]);
    });
  }, []);

  const sendMessage = (e) => {
    e.preventDefault();
    if (message.trim() !== "") {
      const newMessage = {
        author: username,
        message: message,
      };
      // Send the message to the server
      socket.emit("send_message", newMessage);
      setMessages((prevMessages) => [...prevMessages, newMessage]); // Display the message immediately
      setMessage(""); // Clear input field after sending
    }
  };

  return (
    <div className="chat-container">
      {/* Sidebar for Username */}
      <div className="sidebar">
        <h3>Chat Sidebar</h3>
        <p>Logged in as: <strong>{username}</strong></p>
      </div>

      {/* Chat box */}
      <div className="chat-box">
        <div className="messages">
          {messages.map((msg, index) => (
            <div
              key={index}
              className={`message ${msg.author === username ? "outgoing" : "incoming"}`}
            >
              <strong>{msg.author}</strong>: {msg.message}
            </div>
          ))}
        </div>

        {/* Message input */}
        <form onSubmit={sendMessage} className="message-form">
          <input
            type="text"
            value={message}
            onChange={(e) => setMessage(e.target.value)}
            placeholder="Type a message"
          />
          <button type="submit">Send</button>
        </form>
      </div>
    </div>
  );
};

export default Chat;
