import React from 'react';
import '../styles/Chat.css';
import Message from './Message';
import ChatInput from './ChatInput';

function Chat() {
  return (
    <div className="chat">
      <div className="chat-header">
        <img className="contact-img" src="path_to_contact_pic" alt="John Doe" />
        <h3>John Doe</h3>
        <span>Active Now</span>
        <div className="chat-icons">
          <i className="fa fa-star"></i>
          <i className="fa fa-user-plus"></i>
          <i className="fa fa-video"></i>
          <i className="fa fa-phone"></i>
        </div>
      </div>

      <div className="chat-messages">
        <Message from="John Doe" text="Hey!" time="11:12 pm" />
        <Message from="John Doe" text="Hi, how are you doing?" time="11:13 pm" />
        <Message from="Shikha Gupta" text="I'm doing great. How about you?" time="11:14 pm" />
        <Message from="Shikha Gupta" text="Please give me some time." time="11:14 pm" />
      </div>

      <ChatInput />
    </div>
  );
}

export default Chat;
