import React, { useState, useEffect } from "react";
import io from "socket.io-client";
import "../styles/Chat.css";  // Import the CSS file

const socket = io("http://localhost:5000");  // Ensure this matches your backend URL

const Chat = () => {
  const [message, setMessage] = useState("");
  const [messages, setMessages] = useState([]);

  // Listen for messages from the server and update the messages array
  useEffect(() => {
    socket.on("message", (message) => {
      setMessages((prevMessages) => [...prevMessages, message]);
    });

    return () => {
      socket.off("message");
    };
  }, []);

  // Send message to the server
  const sendMessage = (e) => {
    e.preventDefault();
    if (message.trim()) {
      const newMessage = {
        text: message,
        time: new Date().toLocaleTimeString(),
        isSender: true,  // Indicate this message is from the sender
      };
      socket.emit("sendMessage", newMessage);  // Emit to the server
      setMessages((prevMessages) => [...prevMessages, newMessage]);  // Show in chat immediately
      setMessage("");  // Clear the input
    }
  };

  // Handle pressing Enter to send the message
  const handleKeyPress = (e) => {
    if (e.key === 'Enter') {
      sendMessage(e);
    }
  };

  return (
    <div className="chat-container">
      <div className="sidebar">
        <div className="profile-section">
          <h2>Shikha Gupta</h2>
        </div>
        <div className="search-section">
          <input type="text" placeholder="Search" />
        </div>
        <div className="contacts">
          <div className="contact active">
            <p>John Doe</p>
            <span>Now</span>
          </div>
          {/* Add more contacts as needed */}
        </div>
      </div>

      <div className="chat-section">
        <div className="chat-header">
          <h3>John Doe</h3>
          <span>Active Now</span>
        </div>
        <div className="messages">
          {messages.map((msg, index) => (
            <div key={index} className={`message ${msg.isSender ? "sent" : "received"}`}>
              <p>{msg.text}</p>
              <span>{msg.time}</span>
            </div>
          ))}
        </div>
        <div className="input-section">
          <input
            type="text"
            value={message}
            onChange={(e) => setMessage(e.target.value)}
            onKeyPress={handleKeyPress}  // Listen for Enter key press
            placeholder="Type a message"
          />
          <button onClick={sendMessage}>
            <i className="send-icon">Send</i>
          </button>
        </div>
      </div>
    </div>
  );
};

export default Chat;
