import React from 'react';

const Sidebar = ({ users }) => {
  return (
    <div className="sidebar">
      <h3>Active Users</h3>
      {users && users.length > 0 ? (
        <ul>
          {users.map((user, index) => (
            <li key={index}>{user}</li>
          ))}
        </ul>
      ) : (
        <p>No users online</p>
      )}
    </div>
  );
};

export default Sidebar;
