import React, { useEffect, useState } from "react";
import io from "socket.io-client";
import "../styles/Chat.css";


const socket = io("http://localhost:5000");

const Chat = ({ username }) => {
  const [message, setMessage] = useState("");
  const [messages, setMessages] = useState([]);
  const [activeUsers, setActiveUsers] = useState([]);
  const [selectedUser, setSelectedUser] = useState(null); // For private messaging

  useEffect(() => {
    // Join with the provided username
    socket.emit("join", username);

    // Receive active users
    socket.on("active_users", (users) => {
      setActiveUsers(users);
    });

    // Receive private messages
    socket.on("receive_private_message", (data) => {
      setMessages((prevMessages) => [...prevMessages, { sender: data.sender, text: data.message }]);
    });

    return () => {
      socket.disconnect();
    };
  }, [username]);

  const sendMessage = () => {
    if (selectedUser) {
      socket.emit("send_private_message", {
        receiverUsername: selectedUser.username,
        message,
      });
      setMessages((prevMessages) => [...prevMessages, { sender: username, text: message }]);
      setMessage("");
    }
  };

  return (
    <div className="chat-container">
      <div className="sidebar">
        <h3>Active Users</h3>
        <ul>
          {activeUsers.map((user, index) => (
            <li key={index} onClick={() => setSelectedUser(user)}>
              {user.username}
            </li>
          ))}
        </ul>
      </div>

      <div className="chat-box">
        <div className="messages">
          {messages.map((msg, index) => (
            <div key={index} className={msg.sender === username ? "my-message" : "other-message"}>
              <b>{msg.sender}: </b>{msg.text}
            </div>
          ))}
        </div>

        <div className="message-input">
          <input
            type="text"
            placeholder="Type a message..."
            value={message}
            onChange={(e) => setMessage(e.target.value)}
          />
          <button onClick={sendMessage}>Send</button>
        </div>
      </div>
    </div>
  );
};

export default Chat;
