import React, { useState, useEffect } from 'react';
import io from 'socket.io-client';
import Sidebar from './Sidebar'; // Sidebar for active users
import ChatInput from './ChatInput'; // Chat input field

const socket = io('http://localhost:3000'); // Adjust backend URL

const Chat = ({ username }) => {
    const [activeUsers, setActiveUsers] = useState([]);
    const [selectedUser, setSelectedUser] = useState(null);
    const [messages, setMessages] = useState([]);

    useEffect(() => {
        // Join chat with username
        socket.emit('join', username);

        // Listen for updated user list
        socket.on('activeUsers', (users) => {
            setActiveUsers(users);
        });

        // Listen for incoming messages
        socket.on('message', ({ message, from }) => {
            setMessages((prev) => [...prev, { from, message }]);
        });
    }, [username]);

    // Send private message to selected user
    const sendMessage = (message) => {
        if (message && selectedUser) {
            socket.emit('privateMessage', { message, to: selectedUser.socketId });
            setMessages([...messages, { from: 'Me', message }]);
        }
    };

    return (
        <div>
            <h3>Welcome, {username}</h3>
            {/* Sidebar Component */}
            <Sidebar activeUsers={activeUsers} setSelectedUser={setSelectedUser} selectedUser={selectedUser} />

            {/* Chat Interface */}
            {selectedUser ? (
                <div>
                    <h4>Chat with {selectedUser.username}</h4>
                    <div className="messages">
                        {messages.map((msg, index) => (
                            <div key={index} className={msg.from === 'Me' ? 'message-sent' : 'message-received'}>
                                <strong>{msg.from}:</strong> {msg.message}
                            </div>
                        ))}
                    </div>

                    {/* Chat Input Component */}
                    <ChatInput sendMessage={sendMessage} />
                </div>
            ) : (
                <h4>Select a user to start chatting</h4>
            )}
        </div>
    );
};

export default Chat;
