import React, { useState, useEffect } from 'react';
import io from 'socket.io-client';

const socket = io('http://localhost:5000');

const Chat = () => {
  const [username, setUsername] = useState('');
  const [loggedIn, setLoggedIn] = useState(false);
  const [activeUsers, setActiveUsers] = useState([]);
  const [currentChatUser, setCurrentChatUser] = useState(null);
  const [message, setMessage] = useState('');
  const [chatMessages, setChatMessages] = useState([]);

  useEffect(() => {
    socket.on('activeUsers', (users) => {
      setActiveUsers(users);
    });

    socket.on('receiveMessage', (messageData) => {
      if (messageData.from === currentChatUser || messageData.to === currentChatUser) {
        setChatMessages((prevMessages) => [...prevMessages, messageData]);
      }
    });

    return () => {
      socket.off();
    };
  }, [currentChatUser]);

  const handleLogin = () => {
    if (username.trim()) {
      socket.emit('login', username);
      setLoggedIn(true);
    }
  };

  const handleSendMessage = () => {
    if (message.trim() !== '' && currentChatUser) {
      const messageData = { from: username, to: currentChatUser, message };
      socket.emit('sendMessage', messageData);
      setChatMessages((prevMessages) => [...prevMessages, messageData]);
      setMessage('');
    }
  };

  const selectUserToChat = (user) => {
    setCurrentChatUser(user);
    setChatMessages([]); // Clear chat for new user
  };

  return (
    <div className="chat-app">
      {!loggedIn ? (
        <div className="login-page">
          <input
            type="text"
            placeholder="Enter your username"
            value={username}
            onChange={(e) => setUsername(e.target.value)}
          />
          <button onClick={handleLogin}>Login</button>
        </div>
      ) : (
        <div className="chat-container">
          <div className="sidebar">
            <h3>Active Users</h3>
            {activeUsers.map((user, index) => (
              <div
                key={index}
                className={`user ${currentChatUser === user ? 'active' : ''}`}
                onClick={() => selectUserToChat(user)}
              >
                {user}
              </div>
            ))}
          </div>
          <div className="chat-box">
            <div className="chat-messages">
              {chatMessages.map((msg, index) => (
                <div key={index} className={`message ${msg.from === username ? 'me' : 'them'}`}>
                  <strong>{msg.from}: </strong> {msg.message}
                </div>
              ))}
            </div>
            {currentChatUser && (
              <div className="message-input">
                <input
                  type="text"
                  placeholder="Type a message..."
                  value={message}
                  onChange={(e) => setMessage(e.target.value)}
                />
                <button onClick={handleSendMessage}>Send</button>
              </div>
            )}
          </div>
        </div>
      )}
    </div>
  );
};

export default Chat;
