import React, { useState, useEffect } from 'react';
import io from 'socket.io-client';
import "../styles/Chat.css";

// Use the backend server URL
const socket = io('http://localhost:3001');

function Chat({ username }) {
    const [message, setMessage] = useState('');
    const [messages, setMessages] = useState([]);
    const [activeUsers, setActiveUsers] = useState([]);
    const [selectedUser, setSelectedUser] = useState('');

    useEffect(() => {
        // Join the user
        socket.emit('join', username);

        // Update the active user list
        socket.on('updateUserList', (users) => {
            setActiveUsers(users.filter(user => user.username !== username));
        });

        // Listen for incoming private messages
        socket.on('receive_private_message', (data) => {
            setMessages(prevMessages => [...prevMessages, data]);
        });

        // Cleanup on unmount
        return () => {
            socket.off('updateUserList');
            socket.off('receive_private_message');
        };
    }, [username]);

    const sendMessage = (e) => {
        e.preventDefault();
        if (message && selectedUser) {
            socket.emit('send_private_message', {
                sender: username,
                receiver: selectedUser,
                message,
            });
            setMessages(prevMessages => [...prevMessages, { sender: username, message }]);
            setMessage('');
        }
    };

    return (
        <div className="chat-container">
            <h2>Welcome, {username}</h2>
            <div className="user-list">
                <h3>Active Users</h3>
                <ul>
                    {activeUsers.map(user => (
                        <li
                            key={user.socketId}
                            onClick={() => setSelectedUser(user.username)}
                            style={{ cursor: 'pointer', color: selectedUser === user.username ? 'blue' : 'black' }}
                        >
                            {user.username}
                        </li>
                    ))}
                </ul>
            </div>

            {selectedUser && (
                <div className="chat-box">
                    <h3>Chat with {selectedUser}</h3>
                    <div className="messages">
                        {messages.map((msg, index) => (
                            <p key={index}>
                                <strong>{msg.sender === username ? 'Me' : msg.sender}</strong>: {msg.message}
                            </p>
                        ))}
                    </div>
                    <form onSubmit={sendMessage} className="message-form">
                        <input
                            type="text"
                            value={message}
                            onChange={(e) => setMessage(e.target.value)}
                            placeholder="Type a message..."
                        />
                        <button type="submit">Send</button>
                    </form>
                </div>
            )}
        </div>
    );
}

export default Chat;
