import React from 'react';
import './Sidebar.css'; // Sidebar styles

const Sidebar = ({ username, activeUsers, selectUserToChat, currentChatUser }) => {
  return (
    <div className="sidebar">
      <div className="profile">
        <img src="profile.jpg" alt="Profile" />
        <h3>{username}</h3> {/* Display the current user's username */}
      </div>
      <h4>Active Users</h4>
      <ul className="contacts">
        {activeUsers.map((user, index) => (
          <li
            key={index}
            className={`contact ${user === currentChatUser ? 'active' : ''}`}
            onClick={() => selectUserToChat(user)}
          >
            <span>{user}</span>
          </li>
        ))}
      </ul>
    </div>
  );
};

export default Sidebar;
