import React, { useState, useEffect, useRef } from 'react';
import io from 'socket.io-client';
import "../styles/Chat.css";

const socket = io('http://localhost:3001');

function Chat({ username }) {
    const [message, setMessage] = useState('');
    const [messages, setMessages] = useState([]);
    const [activeUsers, setActiveUsers] = useState([]);
    const [selectedUser, setSelectedUser] = useState('');
    const messageEndRef = useRef(null); // Reference for auto-scrolling

    useEffect(() => {
        // Join the chat room
        socket.emit('join', username);

        // Update active users list
        socket.on('updateUserList', (users) => {
            setActiveUsers(users.filter(user => user.username !== username));
        });

        // Handle receiving private messages
        socket.on('receive_private_message', (data) => {
            setMessages(prevMessages => [...prevMessages, data]);
        });

        // Clean up event listeners on unmount
        return () => {
            socket.off('updateUserList');
            socket.off('receive_private_message');
        };
    }, [username]);

    // Auto-scroll to the latest message
    useEffect(() => {
        if (messageEndRef.current) {
            messageEndRef.current.scrollIntoView({ behavior: 'smooth' });
        }
    }, [messages]);

    const sendMessage = (e) => {
        e.preventDefault();
        if (message && selectedUser) {
            socket.emit('send_private_message', {
                sender: username,
                receiver: selectedUser,
                message,
            });
            setMessages(prevMessages => [...prevMessages, { sender: username, message }]);
            setMessage(''); // Clear input field after sending message
        }
    };

    return (
        <div className="chat-container">
            <h2 className="welcome-message">Welcome, {username}</h2>

            {/* Active Users List */}
            <div className="users-list">
                <h3>Active Users</h3>
                <ul>
                    {activeUsers.map(user => (
                        <li
                            key={user.socketId}
                            onClick={() => setSelectedUser(user.username)}
                            className={`user-item ${selectedUser === user.username ? 'selected' : ''}`}
                        >
                            {user.username}
                        </li>
                    ))}
                </ul>
            </div>

            {/* Chat Window */}
            {selectedUser && (
                <div className="chat-window">
                    <h3>Chat with {selectedUser}</h3>
                    <div className="messages">
                        {messages.map((msg, index) => (
                            <p key={index} className={msg.sender === username ? 'my-message' : 'their-message'}>
                                <strong>{msg.sender === username ? 'Me' : msg.sender}:</strong> {msg.message}
                            </p>
                        ))}
                        <div ref={messageEndRef} /> {/* Dummy div for auto-scroll */}
                    </div>

                    {/* Message Input Form */}
                    <form onSubmit={sendMessage} className="message-form">
                        <input
                            type="text"
                            value={message}
                            onChange={(e) => setMessage(e.target.value)}
                            placeholder="Type a message..."
                            className="message-input"
                        />
                        <button type="submit" className="send-button">Send</button>
                    </form>
                </div>
            )}
        </div>
    );
}

export default Chat;
